import{_ as a,c as t,o as r,a2 as l}from"./chunks/framework.CulRYwFH.js";const u=JSON.parse('{"title":"MCP Composer Release Roadmap – August 2025","description":"","frontmatter":{},"headers":[],"relativePath":"guide/roadmap.md","filePath":"guide/roadmap.md"}'),o={name:"guide/roadmap.md"};function i(s,e,d,n,p,m){return r(),t("div",null,e[0]||(e[0]=[l('<h1 id="mcp-composer-release-roadmap-–-august-2025" tabindex="-1">MCP Composer Release Roadmap – August 2025 <a class="header-anchor" href="#mcp-composer-release-roadmap-–-august-2025" aria-label="Permalink to &quot;MCP Composer Release Roadmap – August 2025&quot;">​</a></h1><h2 id="release-name-mcp-composer-v0-1-aug" tabindex="-1">Release Name: MCP Composer v0.1 AUG&quot; <a class="header-anchor" href="#release-name-mcp-composer-v0-1-aug" aria-label="Permalink to &quot;Release Name: MCP Composer v0.1 AUG&quot;&quot;">​</a></h2><p><strong>Target Date:</strong> 2025-08-31<br><strong>Release Type:</strong> Major feature release with multiple parallel tracks.</p><hr><h3 id="release-goals" tabindex="-1">Release Goals <a class="header-anchor" href="#release-goals" aria-label="Permalink to &quot;Release Goals&quot;">​</a></h3><ul><li>Enable Features e.g. Multi-Teanancy , Middleware as plaugins</li><li>Deploy the mcp-composer on solis platform</li><li>Release pypi for mcp-composer</li><li>Release functionality for langlfow 1.6 relelase</li></ul><hr><h3 id="included-features" tabindex="-1">Included Features <a class="header-anchor" href="#included-features" aria-label="Permalink to &quot;Included Features&quot;">​</a></h3><ol><li><p><strong>Middleware Pattern Updates</strong></p><ul><li>Added feature to add middleware as plugin from configuration</li><li>Added semantic similarity checks for prompt filtering, pii reduction, rate limit.</li></ul></li><li><p><strong>Policy-Aware RBAC Enforcement</strong></p><ul><li>HashiCorp Vault HCL-based per-tool access control.</li><li></li></ul></li><li><p><strong>Multi-Tenant MCP Server Support</strong></p><ul><li>Tool naming convention: <code>tenantid_serverid_toolname</code>.</li><li>Tenant-aware resource search with prefix filtering.</li></ul></li><li><p><strong>Plugin System for Middleware</strong></p><ul><li><code>composer add-plugin &lt;repo&gt;</code> CLI for installing plugins.</li><li>Dynamic middleware loading from TOML config.</li></ul></li><li><p><strong>Langflow Integration</strong></p><ul><li>Direct MCP Composer connector node in Langflow.</li><li>Load MCP server tools as Langflow components.</li><li>Execute Langflow workflows with MCP tool calls.</li><li>Support different authentication pattern for mcp server .</li></ul></li></ol><hr><h3 id="parallel-workstreams" tabindex="-1">Parallel Workstreams <a class="header-anchor" href="#parallel-workstreams" aria-label="Permalink to &quot;Parallel Workstreams&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Workstream</th><th>Owner</th><th>Dependencies</th><th>Status</th></tr></thead><tbody><tr><td>Security Middleware</td><td></td><td>Harshicorp team</td><td>In Progress</td></tr><tr><td>RBAC Middleware</td><td>Access Control Team</td><td>Vault dev setup</td><td>Completed</td></tr><tr><td>Multi-Tenancy</td><td>Core MCP Composer Team</td><td>Tool routing updates</td><td>Planned</td></tr><tr><td>Plugin Loader</td><td>Developer Experience Team</td><td>MiddlewareManager update</td><td>Planned</td></tr><tr><td>Deployment on Solis platform</td><td>CI/CD Team</td><td>None</td><td>Completed</td></tr><tr><td>PyPI Release</td><td>Roy</td><td>IBM Open source Legal team</td><td>In Progress</td></tr><tr><td>Langflow Integration</td><td>Langflow team</td><td>IBM Open source Legal team, PyPi Release</td><td>In Progress</td></tr></tbody></table><hr><h1 id="mcp-composer-release-roadmap-–-september-2025" tabindex="-1">MCP Composer Release Roadmap – September 2025 <a class="header-anchor" href="#mcp-composer-release-roadmap-–-september-2025" aria-label="Permalink to &quot;MCP Composer Release Roadmap – September 2025&quot;">​</a></h1><h2 id="release-name-mcp-composer-v0-2-sept" tabindex="-1">Release Name: MCP Composer v0.2 SEPT&quot; <a class="header-anchor" href="#release-name-mcp-composer-v0-2-sept" aria-label="Permalink to &quot;Release Name: MCP Composer v0.2 SEPT&quot;&quot;">​</a></h2><p><strong>Target Date:</strong> 2025-09-30<br><strong>Release Type:</strong> Major feature release with multiple parallel tracks.</p><hr><h3 id="release-goals-1" tabindex="-1">Release Goals <a class="header-anchor" href="#release-goals-1" aria-label="Permalink to &quot;Release Goals&quot;">​</a></h3><ul><li>Release functionality for langlfow 1.7 relelase</li><li>Deploy on Sidekick</li></ul><h3 id="included-features-1" tabindex="-1">Included Features <a class="header-anchor" href="#included-features-1" aria-label="Permalink to &quot;Included Features&quot;">​</a></h3><ol><li><p><strong>Langflow Integration</strong></p><ul><li>Add MCP_Composer component or Update the existing MCP conenction componet to enable <ul><li>Add dynamic tool, server on run time, add tool from open api spec , add middleware from command line</li></ul></li><li>Enable Langflow to perform mcp server orchestration.</li><li>Enable Langflow with carbon chat for Solis chat ui</li></ul></li><li><p><strong>Deploy on Sidekick platform</strong></p><ul><li>Enable agent and mcp server from non MCSP products.</li><li>Orchestrate agents and mcp server for Sidekick</li></ul></li></ol>',21)]))}const g=a(o,[["render",i]]);export{u as __pageData,g as default};
